ValidationRule.errorConditionFormula:
IF( ByPassBln__c = TRUE, OR( AND( ISNEW(), $Profile.Name!= 'Interface Salesforce', $Profile.Name !='Coordenador SAC', $Profile.Name!= 'Administrador do sistema', RecordType.Name = 'Reposição', ISPICKVAL( Reason , 'Produto faltante'),
IF(NOT(OR( ISPICKVAL(Status, 'Aberto'), ISPICKVAL(Status, 'Aguardando nota fiscal de devolução'))) , true, false)
), AND( RecordType.Name = 'Reposição', $Profile.Name!= 'Interface Salesforce', $Profile.Name !='Coordenador SAC', $Profile.Name!= 'Administrador do sistema', ISPICKVAL( Reason , 'Produto faltante'), IF( ISPICKVAL( PRIORVALUE(Status) , 'Aguardando nota fiscal de devolução'), IF(NOT(OR( ISPICKVAL(Status, 'Aguardando nota fiscal de devolução'), ISPICKVAL(Status, 'Cancelado'))) , true, false) , false) ), AND( RecordType.Name = 'Reposição', $Profile.Name!= 'Interface Salesforce', $Profile.Name !='Coordenador SAC', $Profile.Name!= 'Administrador do sistema', ISPICKVAL( Reason , 'Produto faltante'), IF( ISPICKVAL( PRIORVALUE(Status) , 'Aberto'), IF(NOT(OR( ISPICKVAL(Status, 'Aberto'), ISPICKVAL(Status, 'Cancelado'))) , true, false) , false) ), AND( RecordType.Name = 'Reposição', $Profile.Name!= 'Interface Salesforce', $Profile.Name !='Coordenador SAC', $Profile.Name!= 'Administrador do sistema', ISPICKVAL( Reason , 'Produto faltante'), IF( ISPICKVAL( PRIORVALUE(Status) , 'Ordem de reposição criada'), IF(NOT(OR( ISPICKVAL(Status, 'Ordem de reposição criada'), ISPICKVAL(Status, 'Aguardando negociação comercial'), ISPICKVAL(Status, 'QM em andamento'), ISPICKVAL(Status, 'Cancelado'))) , true, false) , false) ) ), FALSE )
